print(type()) -- Output: nil
print(type(nil)) -- Output: nil
print(type(0)) -- Output: number
print(type(9223372036854775807)) -- Output: number
print(type(0.5)) -- Output: number
print(type(1.0/0.0)) -- Output: number
print(type(0.0/0.0)) -- Output: number
print(type("s")) -- Output: string
print(type({})) -- Output: table
print(type(_ENV)) -- Output: table
